CREATE TABLE COURSE_INFO(COURSE_NO NUMERIC(8,0), COST NUMERIC(5,2))
CREATE TABLE STUDENT_INFO(STUDENT_ID NUMERIC(8,0), STUDENT_FIRST_NAME VARCHAR(15), STUDENT_LAST_NAME VARCHAR(15))
