CREATE TABLE ENROLLMENT_INFO(STUDENT_ID NUMERIC(8,0), SECTION_ID NUMERIC(8,0))
CREATE TABLE GRADE_INFO(STUDENT_ID NUMERIC(8,0), SECTION_ID NUMERIC(8,0), GRADE_TYPE_CODE CHAR(2), GRADE_CODE_OCCURANCE NUMERIC(5))